from __future__ import absolute_import, unicode_literalsfrom celery import shared_task,taskfrom opnms.celery import appfrom .saltstack.saltapi import SaltstackAPIimport jsonimport requestsfrom base.apis import salthost_create_or_updatedef salt_host_create_update():    minion_info = SaltstackAPI.list_all_key()    url = 'http://127.0.0.1:8000/api/deploys/v1/salthost'    for minion in minion_info:        search = {            'SerialNumber': minion,        }        api = salthost_create_or_update(opnms_url=url,search=search)        api.main(minion)if __name__ == '__main__':    salthost_create_or_update()